import React, { useEffect, useState } from 'react'
import { Link, Route, Switch, useLocation } from 'react-router-dom'
import ClassOneToThree from './ClassOneToThree'
import ClassThreeToSix from './ClassThreeToSix'
import FieldWork from './FieldWork'
import Steam from './Steam'
import LearnEng from './LearnEng'

const routes = [
  {
    path: '/chuong-trinh-hoc/lop-nha-tre-1-3-tuoi',
    component: ClassOneToThree
  },
  {
    path: '/chuong-trinh-hoc/lop-mau-giao-3-6-tuoi',
    component: ClassThreeToSix
  },
  {
    path: '/chuong-trinh-hoc/chuong-trinh-hoc-anh-quoc',
    component: FieldWork
  },
  {
    path: '/chuong-trinh-hoc/phuong-phap-steam',
    component: Steam
  },
  {
    path: '/chuong-trinh-hoc/hoc-tieng-anh',
    component: LearnEng
  }
]

function Study({label}) {
  const [styleColor, setStyleColor] = useState(
    {
      'letter': '',
      'vision': '',
      'core': '',
      'diff': '',
      'infra': ''
    }
  );

  let location = useLocation();

  useEffect(() => {
    if (location.pathname === '/ve-chung-toi/gioi-thieu/') {
      setStyleColor({
        'letter': 'bg-warning',
        'vision': '',
        'core': '',
        'diff': '',
        'infra': '',
      });
    } else if (location.pathname === '/ve-chung-toi/tam-nhin-su-menh/') {
      setStyleColor({
        'letter': '',
        'vision': 'bg-warning',
        'core': '',
        'diff': '',
        'infra': '',
      });
    } else if (location.pathname === '/ve-chung-toi/gia-tri-cot-loi/') {
      setStyleColor({
        'letter': '',
        'vision': '',
        'core': 'bg-warning',
        'diff': '',
        'infra': '',
      });
    } else if (location.pathname === '/ve-chung-toi/su-khac-biet/') {
      setStyleColor({
        'letter': '',
        'vision': '',
        'core': '',
        'diff': 'bg-warning',
        'infra': ''
      });
    } else if (location.pathname === '/ve-chung-toi/co-so-vat-chat/') {
      setStyleColor({
        'letter': '',
        'vision': '',
        'core': '',
        'diff': '',
        'infra': 'bg-warning'
      });
    } else {
      if (location.pathname === '/ve-chung-toi/doi-ngu/') {
        setStyleColor({
          'letter': '',
          'vision': '',
          'core': '',
          'diff': '',
          'infra': '',
          'team': 'bg-warning'
        });
      } else {
        setStyleColor({
          'letter': '',
          'vision': '',
          'core': '',
          'diff': '',
          'infra': '',
          'team': ''
        });
      }
    }
  }, [])

  return (
    <div className="bg-about d-flex flex-column justify-content-center align-items-center">
      <label className="display-5 p-5 font-weight-bold">{label}</label>
      <div style={{ width: 1100 }}>
        <div className="row mb-5">
          <div className="col-3">
            <div className="shadow p-3 bg-light">
              <div className="d-flex flex-column">
                <Link to='/chuong-trinh-hoc/lop-nha-tre-1-3-tuoi/' className={`text-decoration-none p-2 text-dark ${styleColor.letter}`} type="button">Lớp nhà trẻ</Link>
                <Link to='/chuong-trinh-hoc/lop-mau-giao-3-6-tuoi/' className={`text-decoration-none p-2 text-dark ${styleColor.vision}`}>Lớp mẫu giáo</Link>
                <Link to='/chuong-trinh-hoc/chuong-trinh-hoc-anh-quoc/' className={`text-decoration-none p-2 text-dark ${styleColor.core}`} type="button">Chương trình học quốc tế</Link>
                <Link to='/chuong-trinh-hoc/phuong-phap-steam/' className={`text-decoration-none p-2 text-dark ${styleColor.diff}`} type="button">Phương pháp STEAM</Link>
                <Link to='/chuong-trinh-hoc/hoc-tieng-anh/' className={`text-decoration-none p-2 text-dark ${styleColor.infra}`} type="button">Học tiếng Anh</Link>
              </div>
            </div>
          </div>
          <div className="col">
            <div className="shadow bg-light">
              <Switch>
                {
                  routes.map((route, index) => (
                    <Route
                      key={index}
                      exact path={route.path}
                      children={<route.component />}
                    />
                  ))
                }
              </Switch>
            </div>
          </div>
        </div>
      </div>
    </div >
  )
}

export default Study
